import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ApiService } from '../api.service';

interface User {
  first_name: string;
  last_name: string;
  email: string;
  date_of_birth: string;
}

type SortColumn = keyof User;
type SortDirection = 'asc' | 'desc';

@Component({
  selector: 'app-users-table',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './users-table.component.html',
  styleUrls: ['./users-table.component.css']
})
export class UsersTableComponent implements OnInit {

  users: User[] = [];
  filteredUsers: User[] = [];
  paginatedUsers: User[] = [];

  // Configuraci√≥n de ordenamiento
  sortColumn: SortColumn = 'first_name';
  sortDirection: SortDirection = 'asc';

  // Configuraci√≥n de paginaci√≥n
  currentPage: number = 1;
  pageSize: number = 10;
  totalPages: number = 0;

  // Estado de carga
  isLoading: boolean = false;
  error: string | null = null;

  constructor(private apiService: ApiService) {}

  ngOnInit() {
    this.loadUsers();
  }

  loadUsers() {
    this.isLoading = true;
    this.error = null;

    this.apiService.getUsers().subscribe({
      next: (data) => {
        this.users = data;
        this.filteredUsers = [...data];
        this.applySort();
        this.calculatePagination();
        this.updatePaginatedUsers();
        this.isLoading = false;
      },
      error: (err) => {
        this.error = 'Error al cargar usuarios';
        console.error(err);
        this.isLoading = false;
      }
    });
  }

  // Ordenamiento
  sortBy(column: SortColumn) {
    if (this.sortColumn === column) {
      // Cambiar direcci√≥n si ya est√° ordenado por esta columna
      this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      // Nueva columna, ordenar ascendente
      this.sortColumn = column;
      this.sortDirection = 'asc';
    }

    this.applySort();
    this.currentPage = 1; // Volver a la primera p√°gina
    this.updatePaginatedUsers();
  }

  applySort() {
    this.filteredUsers.sort((a, b) => {
      const aValue = a[this.sortColumn];
      const bValue = b[this.sortColumn];

      let comparison = 0;
      if (aValue > bValue) {
        comparison = 1;
      } else if (aValue < bValue) {
        comparison = -1;
      }

      return this.sortDirection === 'asc' ? comparison : -comparison;
    });
  }

  // Paginaci√≥n
  calculatePagination() {
    this.totalPages = Math.ceil(this.filteredUsers.length / this.pageSize);
  }

  updatePaginatedUsers() {
    const startIndex = (this.currentPage - 1) * this.pageSize;
    const endIndex = startIndex + this.pageSize;
    this.paginatedUsers = this.filteredUsers.slice(startIndex, endIndex);
  }

  goToPage(page: number) {
    if (page >= 1 && page <= this.totalPages) {
      this.currentPage = page;
      this.updatePaginatedUsers();
    }
  }

  previousPage() {
    this.goToPage(this.currentPage - 1);
  }

  nextPage() {
    this.goToPage(this.currentPage + 1);
  }

  changePageSize(event: Event) {
    const select = event.target as HTMLSelectElement;
    this.pageSize = parseInt(select.value, 10);
    this.currentPage = 1;
    this.calculatePagination();
    this.updatePaginatedUsers();
  }

  // Helper para mostrar √≠cono de ordenamiento
  getSortIcon(column: SortColumn): string {
    if (this.sortColumn !== column) {
      return '‚áÖ'; // Sin ordenar
    }
    return this.sortDirection === 'asc' ? '‚Üë' : '‚Üì';
  }

  // Helper para p√°ginas visibles
  getVisiblePages(): number[] {
    const pages: number[] = [];
    const maxVisible = 5;

    if (this.totalPages <= maxVisible) {
      // Mostrar todas las p√°ginas
      for (let i = 1; i <= this.totalPages; i++) {
        pages.push(i);
      }
    } else {
      // Mostrar rango alrededor de la p√°gina actual
      let start = Math.max(1, this.currentPage - 2);
      let end = Math.min(this.totalPages, start + maxVisible - 1);

      if (end - start < maxVisible - 1) {
        start = Math.max(1, end - maxVisible + 1);
      }

      for (let i = start; i <= end; i++) {
        pages.push(i);
      }
    }

    return pages;
  }

  // Helper para informaci√≥n de registros
  getRecordInfo(): string {
    const start = (this.currentPage - 1) * this.pageSize + 1;
    const end = Math.min(this.currentPage * this.pageSize, this.filteredUsers.length);
    return `Mostrando ${start}-${end} de ${this.filteredUsers.length} usuarios`;
  }
}
<div class="users-table-container">
  <div class="header">
    <h2>Usuarios - Tabla Paginada y Ordenable</h2>
    <button (click)="loadUsers()" class="refresh-btn" [disabled]="isLoading">
      {{ isLoading ? 'Cargando...' : 'üîÑ Recargar' }}
    </button>
  </div>

  <!-- Mensaje de error -->
  <div *ngIf="error" class="error-message">
    {{ error }}
  </div>

  <!-- Loading spinner -->
  <div *ngIf="isLoading" class="loading-spinner">
    <div class="spinner"></div>
    <p>Cargando usuarios...</p>
  </div>

  <!-- Tabla de usuarios -->
  <div *ngIf="!isLoading && !error" class="table-wrapper">

    <!-- Controles superiores -->
    <div class="table-controls">
      <div class="page-size-control">
        <label>
          Mostrar:
          <select (change)="changePageSize($event)" [value]="pageSize">
            <option value="5">5</option>
            <option value="10">10</option>
            <option value="20">20</option>
            <option value="50">50</option>
          </select>
          registros
        </label>
      </div>
      <div class="record-info">
        {{ getRecordInfo() }}
      </div>
    </div>

    <!-- Tabla -->
    <table class="users-table">
      <thead>
        <tr>
          <th (click)="sortBy('first_name')" class="sortable">
            Nombre {{ getSortIcon('first_name') }}
          </th>
          <th (click)="sortBy('last_name')" class="sortable">
            Apellido {{ getSortIcon('last_name') }}
          </th>
          <th (click)="sortBy('email')" class="sortable">
            Email {{ getSortIcon('email') }}
          </th>
          <th (click)="sortBy('date_of_birth')" class="sortable">
            Fecha de Nacimiento {{ getSortIcon('date_of_birth') }}
          </th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let user of paginatedUsers">
          <td>{{ user.first_name }}</td>
          <td>{{ user.last_name }}</td>
          <td>{{ user.email }}</td>
          <td>{{ user.date_of_birth }}</td>
        </tr>
        <tr *ngIf="paginatedUsers.length === 0">
          <td colspan="4" class="no-data">No hay usuarios para mostrar</td>
        </tr>
      </tbody>
    </table>

    <!-- Paginaci√≥n -->
    <div class="pagination" *ngIf="totalPages > 1">
      <button 
        (click)="previousPage()" 
        [disabled]="currentPage === 1"
        class="page-btn"
        title="P√°gina anterior">
        ‚Üê Anterior
      </button>

      <button 
        *ngFor="let page of getVisiblePages()"
        (click)="goToPage(page)"
        [class.active]="page === currentPage"
        class="page-btn page-number">
        {{ page }}
      </button>

      <button 
        (click)="nextPage()" 
        [disabled]="currentPage === totalPages"
        class="page-btn"
        title="P√°gina siguiente">
        Siguiente ‚Üí
      </button>
    </div>
  </div>
</div>
